INSERT INTO Cuentas VALUES(ENCRYPTBYKEY(KEY_GUID('LlaveEcriptacion'),@Usuario),ENCRYPTBYKEY(KEY_GUID('LlaveEcriptacion'),@Usuario),'EMPL1','Administrador',1,0);
SET @msg = 'SE CREO AL USUARIO '+@Usuario;
EXEC SP_Msg @msg;
END
CLOSE SYMMETRIC KEY LlaveEcriptacion;
GO

